export function weather(){navigator.geolocation&&navigator.geolocation.getCurrentPosition(showPosition)}async function showPosition(t){await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${t.coords.latitude}&lon=${t.coords.longitude}&appid=172292c16873f81dd92079617fb3e269`).then(t=>t.json()).then(t=>{const e=t.weather[0].icon,i=t.main.humidity,n=t.main.temp,a=(t.main.pressure,`<div id='weather-info-id'> \n\t\t\t\t\t\t\t<div id='weather-img-id'><img src='http://openweathermap.org/img/wn/${e}@2x.png' style='margin-top: -30px;'></div>\n\t\t\t\t\t\t\t\t\x3c!--<div id='weather-desc'><small>${t.weather[0].description}</small></div>--\x3e\n\t\t\t\t\t\t\t\t<div id='weather-meta-id'><small>humidity: ${i} temp: ${n}</small></div>\n\t\t\t\t\t\t\t\n\t\t\t\t\t\t\t</div>`);document.getElementById("nav-middle").innerHTML=a})}weather(),setInterval(weather,18e5);